<template>
    <v-container>
        <v-card class="pa-md-4 mt-5 mx-lg-auto" max-width="900">
             <h2 class="mb-4 text-center font-weight-black black--text">Laporan Pengadaan Produk Tahunan</h2>
             <v-divider></v-divider>
             <v-col class="d-flex" cols="12" sm="6">
                <v-select
                :items="items"
                label="Pilih Tahun"
                dense
                outlined
                class="mt-5"
                v-model="tahun"
                ></v-select>
            </v-col>
            <v-card-actions>
                <v-spacer></v-spacer>
                <v-btn color="#DEB887" @click="cetak()"><v-icon class="mr-4">mdi-printer</v-icon> Cetak Laporan</v-btn>
            </v-card-actions>
        </v-card>
    </v-container>
</template>
<script>
import jsPDF from 'jspdf';
import 'jspdf-autotable'
export default {
    data() {
        return {
            tglTransaksi: new Date().toLocaleString(),
            items: ['2018', '2019', '2020'],
            tahun: '',
            pengadaan: [],
        }
    },
    methods:{
        pengadaanData(){
            var uri = this.$apiUrl + '/laporan/pengadaanTahunan?tahun='+this.tahun
            this.$http.get(uri).then( (response) =>{
                this.pengadaan=response.data[0]
                console.log(this.pengadaan)
            })
        },
        cetak(){
            console.log(this.tahun)
            this.pengadaanData();
            this.konfirm();
        },
        konfirm(){
            this.$confirm("Cetak Laporan?").then(() => {
                this.createPDF();
            });
        },
        createPDF () {
            let pdfName = "Laporan Pengadaan Produk Tahunan"; 
            var doc = new jsPDF('2', 'pt', 'a5', true);

            var img = 'data:image/jpeg;base64,'

            doc.addImage(img, "JPEG", 2, 1, 130, 80);
            
            doc.setFontSize(20)
            doc.text(180, 30, 'KOUVEE PET SHOP')
            doc.setFontSize(11)
            doc.text(150, 45, 'Jalan Moses GatotKaca No.22 Yogyakarta 55281')
            doc.setFontSize(11)
            doc.text(225, 60, 'Telp. (0274) 357735')
            doc.setFontSize(11)
            doc.text(195, 73, 'http://www.sayanghewan.com')
            // urutan line(padding, titik awal garis, panjang, titik akhir)
            doc.setLineWidth(0.5);
            doc.line(0, 80, 420, 80);
            doc.setFontSize(14)
            doc.setFontStyle("bold")
            doc.text(100, 100, 'Laporan Pengadaan Produk Tahunan')
            doc.setFontStyle("normal")
            
            doc.setFontSize(11)
            doc.text(30, 130, 'Tahun : ' + this.tahun)
            // Empty square
            doc.rect(30, 150, 360, 355);
            //header
            doc.setFontSize(11)
            doc.setFontStyle("bold")
            doc.text(37, 163, 'No')
            doc.text(80, 163, 'Bulan')
            doc.text(146, 163, 'Jumlah Pengeluaran')
            doc.setFontStyle("normal")
            //garis vertikal
            doc.setLineWidth(1);
            doc.line(60, 150, 60, 505);
            doc.setLineWidth(1);
            doc.line(133, 150, 133, 505);
            //garis horixontal
            doc.setLineWidth(0.5);
            doc.line(30, 170, 390, 170);
            //No
            doc.setFontSize(11)
            doc.text(40, 186, '1');
            doc.text(40, 215, '2');
            doc.text(40, 242, '3');
            doc.text(40, 270, '4');
            doc.text(40, 298, '5');
            doc.text(40, 328, '6');
            doc.text(40, 355, '7');
            doc.text(40, 382, '8');
            doc.text(40, 410, '9');
            doc.text(37, 439, '10');
            doc.text(37, 467, '11');
            doc.text(37, 495, '12');

            for (let i = 0, j = 20; i < 11; i++) {
                doc.setLineWidth(0.5);
                doc.line(30, 175+j, 390, 175+j);
                j+=28;
            }

            doc.setFontSize(11)
            doc.text(68, 186, 'January');
            doc.text(68, 215, 'February');
            doc.text(68, 242, 'Maret');
            doc.text(68, 270, 'April');
            doc.text(68, 298, 'Mei');
            doc.text(68, 328, 'Juni');
            doc.text(68, 355, 'July');
            doc.text(68, 382, 'Agustus');
            doc.text(68, 410, 'September');
            doc.text(68, 439, 'Oktober');
            doc.text(68, 467, 'November');
            doc.text(68, 495, 'Desember');

            doc.setFontSize(11)
            doc.text(143, 186, 'Rp. '+this.pengadaan.januari);
            doc.text(143, 215, 'Rp. '+this.pengadaan.februari);
            doc.text(143, 242, 'Rp. '+this.pengadaan.maret);
            doc.text(143, 270, 'Rp. '+this.pengadaan.april);
            doc.text(143, 298, 'Rp. '+this.pengadaan.mei);
            doc.text(143, 328, 'Rp. '+this.pengadaan.juni);
            doc.text(143, 355, 'Rp. '+this.pengadaan.july);
            doc.text(143, 382, 'Rp. '+this.pengadaan.agustus);
            doc.text(143, 410, 'Rp. '+this.pengadaan.september);
            doc.text(143, 439, 'Rp. '+this.pengadaan.oktober);
            doc.text(143, 467, 'Rp. '+this.pengadaan.november);
            doc.text(143, 495, 'Rp. '+this.pengadaan.desember);

            doc.setFontStyle("bold")
            doc.setFontSize(13)
            doc.text("Total Transaksi : Rp. "+this.pengadaan.total, 361, 520, null, null, "right");
            doc.setFontStyle("normal")
            doc.setFontSize(10)
            doc.text("Tanggal Cetak : "+this.tglTransaksi, 391, 570, null, null, "right");
            

            doc.setProperties({
                title: "Laporan Pengadaan Tahun "+this.tahun
            });
            var string = doc.output('dataurlnewwindow');
            var embed = "<embed width='100%' height='100%' src='" + string + "'/>"
            var x = window.open();
            x.document.open();
            x.document.write(embed);
            x.document.close();

        },
    },
    mounted(){
        if (localStorage.getItem("token") != null) {
        if(localStorage.getItem("peran")=="Kasir"){
              window.location.replace('/homeKasir')
        }else if(localStorage.getItem("peran")=="Customer Service"){
              window.location.replace('/homeCS')
        }else if(localStorage.getItem("peran")=="Owner"){
              next()
        }
    }
    else{
      window.location.replace('/home')
    }
    },
}
</script>